import base64

def xor(pt,key):
    ct = str()
    for i in range(len(pt)):
        pt_i = ord(pt[i])
        key_i = ord(key[i%len(key)])
        new_i = pt_i ^ key_i
        ct += chr(new_i)
    return ct

ciphertext = base64.b64decode("")
ciphertext = ciphertext.decode('ascii')
plaintext = "When they catch you, there won't be any doubt"

for i in range(len(ciphertext)):
    text = ciphertext[i:i+len(plaintext)]
    result = xor(plaintext,text)
    if "flag" in result:
        print(result)

